import React,{useState} from "react";
import "./expenseform.css";

const ExpenseForm = () => {
    const [titleValue,setEnteredTitle]=useState("");
    const [amountValue,setEnteredAmount]=useState("");
    const [dateValue,setEnteredDate]=useState("");
// const [userInput, setUserInput] = useState({
  //   enteredTitle: '',
  //   enteredAmount: '',
  //   enteredDate: '',
  // });
    const titleChangeHandler=(event)=>{
        setEnteredTitle(event.target.value);
        console.log(event.target.value);

    }
    const amountChangeHandler=(event)=>{
        setEnteredAmount(event.target.value);
        console.log(event.target.value);

    }
    const dateChangeHandler=(event)=>{
        setEnteredDate(event.target.value);
        console.log(event.target.value);

    }
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="amount" min="0.01" step="0.01"></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2022-01-01" max="2025-01-01" onChange={amountChangeHandler}></input>
        </div>
      </div>
        <div className="new-expense__actions"><button type="submit" onChange={dateChangeHandler}>Add Expense</button>
        </div>
    </form>
  );
};

export default ExpenseForm;
